import classes from "../Styles/FileUpload.module.css";
import { useDropzone } from "react-dropzone";

const FileUploadComponent = () => {
  const {acceptedFiles, getRootProps, getInputProps, isDragActive} = useDropzone({accept: {'cvs':[]}})

  return <div className={classes.file_upload_container} {...getRootProps()}>
    <input {...getInputProps()} />
{isDragActive ? <p>Drop the files here...</p> : <p>Drag & drop some files here, or click to select</p>}
  </div>;
};

export default FileUploadComponent;
