import classes from "../Styles/FileUpload.module.css";

const FileUploadComponent = () => {
  const dispatch = useDispatch();
  const uploadStatus = useSelector(
    (state: RootState) => state.fileUpload.uploadStatus
  );

  const handleFileUpload = async (
    event: React.ChangeEvent<HTMLInputElement>
  ) => {
    const file = event.target.files?.[0];
    if (file) {
      dispatch(uploadStart());
      try {
        // Upload the file (e.g., to AWS or a backend service)
        // Simulate success:
        dispatch(uploadSuccess(file));
      } catch (error) {
        dispatch(uploadError("File upload failed"));
      }
    }
  };

  return (
    <div className={classes.file_upload_container}>
      <input type="file" onChange={handleFileUpload} />
      {uploadStatus === "loading" && <p>Uploading...</p>}
      {uploadStatus === "error" && <p>Error uploading file.</p>}
    </div>
  );
};

export default FileUploadComponent;
