import React, { useState, useCallback } from 'react';
import { useDropzone } from 'react-dropzone';
import classes from "./FileUpload.module.css"

const FileUpload: React.FC = () => {
  const [file, setFile] = useState<File | null>(null);
  const [status, setStatus] = useState<'idle' | 'uploading' | 'success' | 'failed'>('idle');

  // Explicitly add type annotation for acceptedFiles
  const onDrop = useCallback((acceptedFiles: File[]) => {
    if (acceptedFiles.length) {
      setFile(acceptedFiles[0]);
      setStatus('uploading');
      // Simulate upload delay
      setTimeout(() => setStatus('success'), 2000);  
    }
  }, []);

  const { getRootProps, getInputProps } = useDropzone({ onDrop, accept: '.csv' });

  return (
    <div>
      <div {...getRootProps()} className={classes.file_upload_container}>
        <input {...getInputProps()} />
        <p>Drag & drop a CSV file here, or click to select one</p>
      </div>
      {file && <p>Selected File: {file.name}</p>}
      {status === 'uploading' && <p>Uploading...</p>}
      {status === 'success' && <p>Upload Successful!</p>}
    </div>
  );
};

export default FileUpload;
