import { createSlice, PayloadAction } from '@reduxjs/toolkit';

interface InsightsState {
  insights: string | null;
  insightsStatus: 'idle' | 'loading' | 'success' | 'error';
  error: string | null;
}

const initialState: InsightsState = {
  insights: null,
  insightsStatus: 'idle',
  error: null,
};

const insightsSlice = createSlice({
  name: 'insights',
  initialState,
  reducers: {
    insightsStart(state) {
      state.insightsStatus = 'loading';
      state.error = null;
    },
    insightsSuccess(state, action: PayloadAction<string>) {
      state.insights = action.payload;
      state.insightsStatus = 'success';
    },
    insightsError(state, action: PayloadAction<string>) {
      state.error = action.payload;
      state.insightsStatus = 'error';
    },
  },
});

export const { insightsStart, insightsSuccess, insightsError } = insightsSlice.actions;
export default insightsSlice.reducer;
